def decode_base64_image(data):
    return PhotoImage(data=base64.b64decode(data))


# ---------------------------- TIMER RESET ------------------------------- #

def reset_timer():
    window.after_cancel(timer)
    start_button.config(state=NORMAL)
    timer_label.config(text="Timer", fg=GREEN)
    global reps
    reps = 0
    canvas.itemconfig(timer_text, text=f"00:00")


def play_sound():
    pygame.mixer.init()
    pygame.mixer.music.load(io.BytesIO(base64.b64decode(SOUND_MP3_BASE64)))  # Replace with the actual path to your
    # sound file
    pygame.mixer.music.play()


# ---------------------------- TIMER MECHANISM ------------------------------- #

def start_timer(reps_val=1):
    play_sound()
    global reps
    global check

    reps = reps_val
    if reps_val == 0:
        return
    if reps in (1, 3, 5, 7):
        work_sec = WORK_MIN * 60
        timer_label.config(text="Work", font=(FONT_NAME, 45, "bold"), fg=GREEN)
        count_down(work_sec)
    if reps in (2, 4, 6):
        done_label.config(text=check, fg=GREEN)
        check += " âœ”"
        short_break_sec = SHORT_BREAK_MIN * 60
        timer_label.config(text="Break", fg=RED)
        count_down(short_break_sec)

        done_label.grid(row=3, column=1)
    if reps == 8:
        done_label.config(text=check, fg=GREEN)
        check += " âœ”"
        long_break_sec = LONG_BREAK_MIN * 60
        timer_label.config(text="Break", fg=RED)
        count_down(long_break_sec)


# ---------------------------- COUNTDOWN MECHANISM ------------------------------- #
def count_down(count):
    start_button.config(state=DISABLED)
    min_text = math.floor(count / 60)
    sec_text = count % 60
    sec_text = int(sec_text)

    global reps
    if sec_text < 10:
        sec_text = f"0{str(sec_text)}"

    canvas.itemconfig(timer_text, text=f"{int(min_text)}:{sec_text}")
    print(f"{int(min_text)}:{sec_text}")
    if f"{int(min_text)}:{sec_text}" == f"{0}:00":
        reps += 1

        if reps < 9:
            start_timer(reps)
        else:
            play_sound()
            canvas.itemconfig(timer_text, text=f"Session Finished!", font=(FONT_NAME, 14, "bold"))
            return

    if count > 0:
        global timer
        timer = window.after(1000, count_down, count - 1)


